import { Injectable } from '@angular/core';
import { Observable, catchError, of } from 'rxjs'
import { map } from 'rxjs/operators'
import { DrinkInterface } from '../types/drink.interface';
import { HttpService } from 'src/app/services/http.service';

@Injectable({
  providedIn: 'root'
})
export class DrinksService {

  constructor(private httpService: HttpService) { }

  getBeers(): Observable<DrinkInterface[]> {
    return this.httpService.get("https://random-data-api.com/api/v2/beers?size=500").pipe(
      map((response) => {
        // Transform response to desired format
        return response.map((drink:any) => ({
          id: drink.id.toString(),
          type: 'Beer',
          name: drink.name,
          brand: drink.brand
        }));
      }),
      catchError(() => {
        // Return an observable with a default value in case of an error
        return of([]);
      })
    );
  }


}
