import { Component, OnInit } from '@angular/core';
import { select, Store } from '@ngrx/store';
import { errorSelector, isLoadingSelector, beersSelector } from '../../store/selectors';
import { AppStateInterface } from 'src/app/types/appState.interface';
import { Observable } from "rxjs"

import { DrinkInterface } from '../../types/drink.interface';
import * as DrinksActions from './../../store/actions'

@Component({
  selector: 'app-drinks',
  templateUrl: './drinks.component.html',
  styleUrls: ['./drinks.component.css']
})
export class DrinksComponent implements OnInit{

  isLoading$!: Observable<boolean>;
  beers$!: Observable<DrinkInterface[]>;
  error$!: Observable<string | null>;

  constructor(private store: Store<AppStateInterface>){
    this.isLoading$ = this.store.pipe(select(isLoadingSelector));
    this.error$ = this.store.pipe(select(errorSelector));
    this.beers$ = this.store.pipe(select(beersSelector));
  }


  ngOnInit(): void {
    this.store.dispatch(DrinksActions.getBeers())
  }

}
