import { createAction, props } from "@ngrx/store";
import { DrinkInterface } from "../types/drink.interface";


export enum DrinkActions {
    GET_BEERS = '[Drinks] Get Beers',
    GET_BEERS_SUCCESS = '[Drinks] Get Beers Success',
    GET_BEERS_FAILURE = '[Drinks] Get Beers Failure'
}


export const getBeers = createAction(
    DrinkActions.GET_BEERS
);

export const getBeersSuccess = createAction(
    DrinkActions.GET_BEERS_SUCCESS,
    props<{ beers: DrinkInterface[] }>()
);


export const getBeersFailure = createAction(
    DrinkActions.GET_BEERS,
    props<{ error: string }>()
);
